import React, { useState, useEffect } from 'react';
import { 
  Stethoscope, 
  User, 
  Calendar, 
  LogOut, 
  Search, 
  Edit2, 
  Trash2, 
  PlusCircle 
} from "lucide-react";

import { db } from './firebase';
import { doc, addDoc, collection, updateDoc, deleteDoc, getDocs } from 'firebase/firestore';

const Dashboard = () => {
  const [activeTab, setActiveTab] = useState("appointments");
  const [appointments, setAppointments] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingAppointment, setEditingAppointment] = useState(null);

  // Appointment form state
  const [newAppointment, setNewAppointment] = useState({
    specialty: '',
    doctor: '',
    date: '',
    time: '',
    notes: '',
    status: 'Pendiente'
  });

  // Fetch appointments from Firebase
  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "appointments"));
        const fetchedAppointments = querySnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setAppointments(fetchedAppointments);
      } catch (error) {
        console.error("Error fetching appointments: ", error);
      }
    };

    fetchAppointments();
  }, []);

  // Add new appointment
  const handleAddAppointment = async (e) => {
    e.preventDefault();
    try {
      const docRef = await addDoc(collection(db, "appointments"), {
        ...newAppointment,
        status: "Pendiente"
      });

      setAppointments([
        ...appointments, 
        { id: docRef.id, ...newAppointment, status: "Pendiente" }
      ]);

      // Reset form
      setNewAppointment({
        specialty: '',
        doctor: '',
        date: '',
        time: '',
        notes: '',
        status: 'Pendiente'
      });
      setIsModalOpen(false);
    } catch (error) {
      console.error("Error adding appointment: ", error);
    }
  };

  // Edit appointment
  const handleEditAppointment = async (e) => {
    e.preventDefault();
    try {
      const appointmentRef = doc(db, "appointments", editingAppointment.id);
      await updateDoc(appointmentRef, newAppointment);

      setAppointments(appointments.map(app => 
        app.id === editingAppointment.id 
          ? { ...app, ...newAppointment } 
          : app
      ));

      setEditingAppointment(null);
      setIsModalOpen(false);
    } catch (error) {
      console.error("Error editing appointment: ", error);
    }
  };

  // Delete appointment
  const handleDeleteAppointment = async (id) => {
    try {
      await deleteDoc(doc(db, "appointments", id));
      setAppointments(appointments.filter(app => app.id !== id));
    } catch (error) {
      console.error("Error deleting appointment: ", error);
    }
  };

  // Appointment Modal Component
  const AppointmentModal = () => (
    <div className={`fixed inset-0 z-50 ${isModalOpen ? 'block' : 'hidden'} bg-black bg-opacity-50 flex items-center justify-center`}>
      <div className="bg-white p-6 rounded-lg w-96 max-w-full">
        <h2 className="text-xl font-bold mb-4">
          {editingAppointment ? 'Editar Cita' : 'Nueva Cita'}
        </h2>
        <form onSubmit={editingAppointment ? handleEditAppointment : handleAddAppointment}>
          <div className="space-y-4">
            <input
              type="text"
              placeholder="Especialidad"
              value={newAppointment.specialty}
              onChange={(e) => setNewAppointment({...newAppointment, specialty: e.target.value})}
              className="w-full p-2 border rounded"
              required
            />
            <input
              type="text"
              placeholder="Doctor"
              value={newAppointment.doctor}
              onChange={(e) => setNewAppointment({...newAppointment, doctor: e.target.value})}
              className="w-full p-2 border rounded"
              required
            />
            <div className="grid grid-cols-2 gap-2">
              <input
                type="date"
                value={newAppointment.date}
                onChange={(e) => setNewAppointment({...newAppointment, date: e.target.value})}
                className="w-full p-2 border rounded"
                required
              />
              <input
                type="time"
                value={newAppointment.time}
                onChange={(e) => setNewAppointment({...newAppointment, time: e.target.value})}
                className="w-full p-2 border rounded"
                required
              />
            </div>
            <textarea
              placeholder="Notas"
              value={newAppointment.notes}
              onChange={(e) => setNewAppointment({...newAppointment, notes: e.target.value})}
              className="w-full p-2 border rounded"
              rows="3"
            />
            <div className="flex justify-end space-x-2">
              <button 
                type="button" 
                onClick={() => {
                  setIsModalOpen(false);
                  setEditingAppointment(null);
                  setNewAppointment({specialty: '', doctor: '', date: '', time: '', notes: '', status: 'Pendiente'});
                }} 
                className="px-4 py-2 bg-gray-200 rounded"
              >
                Cancelar
              </button>
              <button 
                type="submit" 
                className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
              >
                {editingAppointment ? 'Actualizar' : 'Crear'}
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );

  // Navbar Component
  const Navbar = () => (
    <nav className="bg-white shadow-md mb-6">
      <div className="container mx-auto px-4 py-3 flex justify-between items-center">
        <div className="flex items-center">
          <Stethoscope className="w-8 h-8 text-blue-600 mr-3" />
          <h1 className="text-2xl font-bold text-blue-600">MediCenter</h1>
        </div>

        <div className="flex items-center space-x-4">
          {[
            { icon: Calendar, label: "Mis Citas", value: "appointments" },
            { icon: Search, label: "Buscar Citas", value: "search" },
            { icon: User, label: "Perfil", value: "profile" },
          ].map((item) => (
            <button
              key={item.value}
              onClick={() => setActiveTab(item.value)}
              className={`flex items-center space-x-2 px-3 py-2 rounded-md transition-colors ${
                activeTab === item.value 
                  ? "bg-blue-100 text-blue-600 font-semibold" 
                  : "hover:bg-gray-100 text-gray-700"
              }`}
            >
              <item.icon className="w-5 h-5" />
              <span className="hidden md:inline">{item.label}</span>
            </button>
          ))}

          <button 
            className="text-red-600 hover:bg-red-50 rounded-md p-2"
            title="Cerrar Sesión"
          >
            <LogOut className="w-5 h-5" />
          </button>
        </div>
      </div>
    </nav>
  );

  // Appointments Section Component
  const AppointmentsSection = () => (
    <div className="bg-white rounded-lg shadow-lg p-6">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-blue-600">Mis Citas Médicas</h2>
        <button 
          className="flex items-center space-x-2 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition-colors"
          onClick={() => {
            setIsModalOpen(true);
            setEditingAppointment(null);
          }}
        >
          <PlusCircle className="w-5 h-5" />
          <span>Nueva Cita</span>
        </button>
      </div>

      {appointments.length === 0 ? (
        <div className="text-center text-gray-500 py-10">
          No hay citas programadas
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {appointments.map((appointment) => (
            <div 
              key={appointment.id} 
              className="border rounded-lg p-4 hover:shadow-md transition-shadow"
            >
              <div className="flex justify-between items-start mb-3">
                <div>
                  <h3 className="text-lg font-semibold text-blue-500">
                    {appointment.specialty}
                  </h3>
                  <p className="text-gray-600 text-sm">
                    {appointment.doctor}
                  </p>
                </div>
                <span
                  className={`px-3 py-1 rounded-full text-xs font-medium ${
                    appointment.status === "Confirmada"
                      ? "bg-green-100 text-green-800"
                      : "bg-yellow-100 text-yellow-800"
                  }`}
                >
                  {appointment.status}
                </span>
              </div>

              <div className="flex justify-between text-sm text-gray-500 mb-3">
                <div className="flex items-center space-x-2">
                  <Calendar className="w-4 h-4" />
                  <span>{appointment.date}</span>
                </div>
                <span>{appointment.time}</span>
              </div>

              {appointment.notes && (
                <div className="bg-gray-50 p-2 rounded text-sm text-gray-700 mb-3">
                  <p className="font-medium">Notas:</p>
                  <p>{appointment.notes}</p>
                </div>
              )}

              <div className="flex space-x-2">
                <button 
                  className="btn btn-outline-primary flex-1 flex items-center justify-center space-x-2"
                  onClick={() => {
                    setEditingAppointment(appointment);
                    setNewAppointment({
                      specialty: appointment.specialty,
                      doctor: appointment.doctor,
                      date: appointment.date,
                      time: appointment.time,
                      notes: appointment.notes,
                      status: appointment.status
                    });
                    setIsModalOpen(true);
                  }}
                >
                  <Edit2 className="w-4 h-4" />
                  <span>Editar</span>
                </button>
                <button 
                  className="btn btn-outline-danger flex-1 flex items-center justify-center space-x-2"
                  onClick={() => handleDeleteAppointment(appointment.id)}
                >
                  <Trash2 className="w-4 h-4" />
                  <span>Eliminar</span>
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );

  // Placeholder components for other tabs
  const SearchSection = () => (
    <div className="bg-white rounded-lg shadow-lg p-6">
      <h2 className="text-2xl font-bold text-blue-600 mb-4">Buscar Citas</h2>
      <p className="text-gray-500">Próximamente: Funcionalidad de búsqueda de citas</p>
    </div>
  );

  const ProfileSection = () => (
    <div className="bg-white rounded-lg shadow-lg p-6">
      <h2 className="text-2xl font-bold text-blue-600 mb-4">Perfil de Usuario</h2>
      <p className="text-gray-500">Próximamente: Detalles del perfil de usuario</p>
    </div>
  );

  // Render main dashboard
  return (
    <div className="min-h-screen bg-gray-100">
      {/* Navbar */}
      <Navbar />

      {/* Appointment Modal */}
      <AppointmentModal />

      {/* Main Content Area */}
      <div className="container mx-auto px-4">
        {activeTab === "appointments" && <AppointmentsSection />}
        {activeTab === "search" && <SearchSection />}
        {activeTab === "profile" && <ProfileSection />}
      </div>
    </div>
  );
};

export default Dashboard;
