import React, { useEffect, useState } from "react";
import { db } from './firebase';
import { doc, addDoc, collection, updateDoc, deleteDoc, getDocs } from 'firebase/firestore';

const Dashboard = () => {
  const [nombre, setNombre] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [precio, setPrecio] = useState('');
  const [fetchData, setFetchData] = useState([]);
  const [id, setId] = useState('');

  const dbref = collection(db, 'crud-React');

  const add = async () => {
    if (!nombre || !descripcion || !precio) {
      alert('Por favor, completa todos los campos');
      return;
    }
    try {
      const addata = await addDoc(dbref, { Nombre: nombre, Precio: precio, Descripcion: descripcion });
      alert("Los Datos se Agregaron Correctamente");
      fetch();  // Actualizar datos sin recargar la página
    } catch (error) {
      alert("Los Datos no se pudieron Agregar, intenta de nuevo");
      console.error(error);
    }
  };

  const fetch = async () => {
    const snapshot = await getDocs(dbref);
    const fetchdata = snapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
    setFetchData(fetchdata);
  };

  useEffect(() => {
    fetch();
  }, []);

  const passData = async (id) => {
    const matchId = fetchData.find((data) => data.id === id);
    setNombre(matchId.Nombre);
    setPrecio(matchId.Precio);
    setDescripcion(matchId.Descripcion);
    setId(matchId.id);
  };

  const update = async () => {
    if (!nombre || !descripcion || !precio) {
      alert('Por favor, completa todos los campos');
      return;
    }
    const updateref = doc(dbref, id);
    try {
      await updateDoc(updateref, { Nombre: nombre, Precio: precio, Descripcion: descripcion });
      alert('Actualizado correctamente');
      fetch();  // Actualizar datos sin recargar la página
    } catch (error) {
      alert('Hubo un problema al actualizar los datos');
      console.error(error);
    }
  };

  const del = async (id) => {
    const delref = doc(dbref, id);
    try {
      await deleteDoc(delref);
      alert('Se borró correctamente la información');
      fetch();  // Actualizar datos sin recargar la página
    } catch (error) {
      alert('No se borró la información. Inténtalo de nuevo.');
      console.error(error);
    }
  };

  return (
    <>
      <div className='form_container'>
        <h2>Añadir o Actualizar Producto</h2>
        <div className="box">
          <input
            type="text"
            placeholder="Nombre"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
          />
        </div>
        <div className="box">
          <input
            type="number"
            placeholder="Precio"
            value={precio}
            onChange={(e) => setPrecio(e.target.value)}
          />
        </div>
        <div className="box">
          <input
            type="text"
            placeholder="Descripcion"
            value={descripcion}
            onChange={(e) => setDescripcion(e.target.value)}
          />
        </div>
        <button onClick={add}>Añadir</button>
        <button onClick={update} disabled={!id}>Actualizar</button>
      </div>

      <div className="database">
        <h2>Base de Datos CRUD</h2>
        <div className="container">
          {fetchData.map((data) => (
            <div className="box" key={data.id}>
              <h2>Nombre: {data.Nombre}</h2>
              <h2>Precio: {data.Precio}</h2>
              <h2>Descripción: {data.Descripcion}</h2>
              <button onClick={() => passData(data.id)}>Actualizar</button>
              <button onClick={() => del(data.id)}>Eliminar</button>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default Dashboard;
