import React, { useState, useEffect } from 'react';
import { Stethoscope, User, Calendar, LogOut, Search, X } from 'lucide-react';
import { db } from './firebase'; // Importa la instancia de Firestore

const MedicalDashboardPreview = () => {
  const [activeTab, setActiveTab] = useState('appointments');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    const fetchAppointments = async () => {
      const snapshot = await db.collection('appointments').get();
      const fetchedAppointments = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setAppointments(fetchedAppointments);
    };

    fetchAppointments();
  }, []);

  const handleNewAppointment = async (specialty, doctor, date, time) => {
    await db.collection('appointments').add({
      specialty,
      doctor,
      date,
      time,
      status: 'Pendiente' // Estado por defecto
    });
    setIsModalOpen(false);
    // Re-fetch appointments after adding a new one
    const snapshot = await db.collection('appointments').get();
    const fetchedAppointments = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    setAppointments(fetchedAppointments);
  };

  const renderAppointmentsSection = () => (
    <div className="bg-white shadow-md rounded-lg p-6">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-bold text-blue-600">Mis Citas Médicas</h2>
        <button 
          className="bg-green-500 text-white px-4 py-2 rounded-md flex items-center"
          onClick={() => setIsModalOpen(true)}
        >
          <Calendar className="mr-2" size={20} /> 
          Nueva Cita
        </button>
      </div>

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
        {appointments.map((appointment) => (
          <div 
            key={appointment.id} 
            className="border rounded-lg p-4 hover:shadow-md transition-all"
          >
            <div className="flex justify-between mb-2">
              <span className="font-bold text-blue-600">{appointment.specialty}</span>
              <span 
                className={`
                  px-2 py-1 rounded-full text-xs 
                  ${appointment.status === 'Confirmada' 
                    ? 'bg-green-100 text-green-800' 
                    : 'bg-yellow-100 text-yellow-800'
                  }
                `}
              >
                {appointment.status}
              </span>
            </div>
            <p>Dr(a). {appointment.doctor}</p>
            <div className="flex justify-between mt-2 text-gray-600">
              <span>{appointment.date}</span>
              <span>{appointment.time}</span>
            </div>
            <div className="flex mt-3 gap-2">
              <button className="w-full bg-blue-100 text-blue-700 py-2 rounded-md">
                Detalles
              </button>
              <button className="w-full bg-red-100 text-red-700 py-2 rounded-md">
                Cancelar
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const renderModal = () => (
    isModalOpen && (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-white rounded-lg p-6 w-96">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-bold text-blue-600">Agendar Nueva Cita</h2>
            <button onClick={() => setIsModalOpen(false)}>
              <X className="text-gray-600" />
            </button>
          </div>

          <div className="space-y-4">
            {/* Campos para ingresar especialidad, médico, fecha y hora */}
            {/* Para simplificar, se están usando valores fijos; reemplaza con componentes controlados */}
            <select id="specialty" className="w-full p-2 border rounded-md">
              <option>Seleccionar Especialidad</option>
              <option>Cardiología</option>
              <option>Dermatología</option>
              <option>Pediatría</option>
            </select>

            <select id="doctor" className="w-full p-2 border rounded-md">
              <option>Seleccionar Médico</option>
              <option>Dr. Carlos Mendoza</option>
              <option>Dra. Ana Ruiz</option>
            </select>

            <input 
              type="date" 
              id="date"
              className="w-full p-2 border rounded-md"
            />

            <input 
              type="time" 
              id="time"
              className="w-full p-2 border rounded-md"
            />

            <button 
              onClick={() => handleNewAppointment(
                document.getElementById("specialty").value,
                document.getElementById("doctor").value,
                document.getElementById("date").value,
                document.getElementById("time").value
              )}
              className="w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700"
            >
              Confirmar Cita
            </button>
          </div>
        </div>
      </div>
    )
  );

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      {/* Sidebar y contenido principal permanecen sin cambios */}
      {renderAppointmentsSection()}
      {renderModal()}
    </div>
  );
};

export default MedicalDashboardPreview;
