import React, { useState, useEffect } from 'react';
import { Stethoscope, User, Calendar, LogOut, Search, Edit2, Trash2, PlusCircle } from "lucide-react";
import { db } from './firebase';
import { doc, addDoc, collection, updateDoc, deleteDoc, getDocs } from 'firebase/firestore';

const Dashboard = () => {
    const [activeTab, setActiveTab] = useState("appointments");
    const [appointments, setAppointments] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingAppointment, setEditingAppointment] = useState(null);
    const [newAppointment, setNewAppointment] = useState({
        specialty: '',
        doctor: '',
        date: '',
        time: '',
        notes: '',
        status: 'Pendiente'
    });

    useEffect(() => {
        const fetchAppointments = async () => {
            try {
                const querySnapshot = await getDocs(collection(db, "appointments"));
                const fetchedAppointments = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
                setAppointments(fetchedAppointments);
            } catch (error) {
                console.error("Error fetching appointments: ", error);
            }
        };
        fetchAppointments();
    }, []);

    const handleAddAppointment = async (e) => {
        e.preventDefault();
        try {
            const docRef = await addDoc(collection(db, "appointments"), { ...newAppointment });
            setAppointments([...appointments, { id: docRef.id, ...newAppointment }]);
            setNewAppointment({ specialty: '', doctor: '', date: '', time: '', notes: '', status: 'Pendiente' });
            setIsModalOpen(false);
        } catch (error) {
            console.error("Error adding appointment: ", error);
        }
    };

    const handleEditAppointment = async (e) => {
        e.preventDefault();
        try {
            const appointmentRef = doc(db, "appointments", editingAppointment.id);
            await updateDoc(appointmentRef, newAppointment);
            setAppointments(appointments.map(app => app.id === editingAppointment.id ? { ...app, ...newAppointment } : app));
            setEditingAppointment(null);
            setIsModalOpen(false);
        } catch (error) {
            console.error("Error editing appointment: ", error);
        }
    };

    const handleDeleteAppointment = async (id) => {
        try {
            await deleteDoc(doc(db, "appointments", id));
            setAppointments(appointments.filter(app => app.id !== id));
        } catch (error) {
            console.error("Error deleting appointment: ", error);
        }
    };

    const renderSidebar = () => (
        <div className="bg-white rounded-lg shadow-lg p-5">
            <div className="flex items-center mb-6">
                <Stethoscope className="w-8 h-8 text-blue-600 mr-3" />
                <h1 className="text-2xl font-bold text-blue-600">MediCenter</h1>
            </div>
            <nav className="space-y-2">
                {[ 
                    { icon: Calendar, label: "Mis Citas", value: "appointments" },
                    { icon: Search, label: "Buscar Citas", value: "search" },
                    { icon: User, label: "Perfil", value: "profile" },
                ].map((item) => (
                    <button key={item.value} onClick={() => setActiveTab(item.value)} className={`w-full text-left py-2 px-3 rounded-md transition-colors flex items-center space-x-2 ${activeTab === item.value ? "bg-blue-100 text-blue-600 font-semibold" : "hover:bg-gray-100 text-gray-700"}`}>
                        <item.icon className="w-5 h-5" />
                        <span>{item.label}</span>
                    </button>
                ))}
                <div className="border-t mt-4 pt-2">
                    <button className="w-full text-left py-2 px-3 rounded-md text-red-600 hover:bg-red-50 flex items-center space-x-2">
                        <LogOut className="w-5 h-5" />
                        <span>Cerrar Sesión</span>
                    </button>
                </div>
            </nav>
        </div>
    );

    const renderAppointmentModal = () => (
        <div className={`fixed inset-0 z-50 ${isModalOpen ? 'block' : 'hidden'} bg-black bg-opacity-50 flex items-center justify-center`}>
            <div className="bg-white p-6 rounded-lg w-full max-w-md">
                <h2 className="text-xl font-bold mb-4">{editingAppointment ? 'Editar Cita' : 'Nueva Cita'}</h2>
                <form onSubmit={editingAppointment ? handleEditAppointment : handleAddAppointment}>
                    <input type="text" placeholder="Especialidad" value={newAppointment.specialty} onChange={(e) => setNewAppointment({ ...newAppointment, specialty: e.target.value })} className="w-full mb-2 p-2 border rounded" required />
                    <input type="text" placeholder="Doctor" value={newAppointment.doctor} onChange={(e) => setNewAppointment({ ...newAppointment, doctor: e.target.value })} className="w-full mb-2 p-2 border rounded" required />
                    <input type="date" value={newAppointment.date} onChange={(e) => setNewAppointment({ ...newAppointment, date: e.target.value })} className="w-full mb-2 p-2 border rounded" required />
                    <input type="time" value={newAppointment.time} onChange={(e) => setNewAppointment({ ...newAppointment, time: e.target.value })} className="w-full mb-2 p-2 border rounded" required />
                    <textarea placeholder="Notas" value={newAppointment.notes} onChange={(e) => setNewAppointment({ ...newAppointment, notes: e.target.value })} className="w-full mb-2 p-2 border rounded"></textarea>
                    <div className="flex justify-end space-x-2">
                        <button type="button" onClick={() => { setIsModalOpen(false); setEditingAppointment(null); setNewAppointment({ specialty: '', doctor: '', date: '', time: '', notes: '', status: 'Pendiente' }); }} className="px-4 py-2 bg-gray-200 rounded">Cancelar</button>
                        <button type="submit" className="px-4 py-2 bg-blue-500 text-white rounded">{editingAppointment ? 'Actualizar' : 'Crear'}</button>
                    </div>
                </form>
            </div>
        </div>
    );

    const renderAppointmentsSection = () => (
        <div className="bg-white rounded-lg shadow-lg p-6">
            <div className="flex justify-between items-center mb-6">
                <h2 className="text-2xl font-bold text-blue-600">Mis Citas Médicas</h2>
                <button onClick={() => { setIsModalOpen(true); setEditingAppointment(null); }} className="btn btn-primary flex items-center space-x-2 hover:bg-blue-700 transition-colors">
                    <PlusCircle className="w-5 h-5" />
                    <span>Nueva Cita</span>
                </button>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {appointments.map((appointment) => (
                    <div key={appointment.id} className="border rounded-lg p-4 hover:shadow-md transition-shadow">
                        <div className="flex justify-between items-start mb-3">
                            <div>
                                <h3 className="text-lg font-semibold text-blue-500">{appointment.specialty}</h3>
                                <p className="text-gray-600 text-sm">{appointment.doctor}</p>
                            </div>
                            <span className={`px-3 py-1 rounded-full text-xs font-medium ${appointment.status === "Confirmada" ? "bg-green-100 text-green-800" : "bg-yellow-100 text-yellow800"}`}>{appointment.status}</span>
                        </div>
                        <div className="flex justify-between text-sm text-gray500 mb3">
                            <div className="flex items-center space-x=2">
                                <Calendar className="w4 h4"/>
                                <span>{appointment.date}</span>
                            </div>
                            <span>{appointment.time}</span>
                        </div>
                        {appointment.notes && (
                            <div className="bg-gray50 p=2 rounded text-sm text-gray700 mb3">
                                <p className="font-medium">Notas:</p>
                                <p>{appointment.notes}</p>
                            </div>
                        )}
                        <div className="flex space-x=2">
                            <button className="btn btn-outline-primary flex1 flex items-center justify-center space-x=2" onClick={() => { 
                                    setEditingAppointment(appointment); 
                                    setNewAppointment({ specialty: appointment.specialty,
                                                        doctor: appointment.doctor,
                                                        date: appointment.date,
                                                        time: appointment.time,
                                                        notes: appointment.notes,
                                                        status: appointment.status }); 
                                    setIsModalOpen(true); 
                                }}>
                                <Edit2 className="w4 h4"/>
                                <span>Editar</span>
                            </button>
                            <button className="btn btn-outline-danger flex1 flex items-center justify-center space-x=2" onClick={() => handleDeleteAppointment(appointment.id)}>
                                <Trash2 className="w4 h4"/>
                                <span>Eliminar</span>
                            </button>
                        </div>  
                    </div> 
                ))}
            </div> 
        </div> 
    );

    return (
        <div className="min-h-screen bg-gray100 p6">
            {renderAppointmentModal()}
            <div className="grid grid-cols1 md:grid-cols4 gap6 h-full">
                <div className="mdcol-span1">{renderSidebar()}</div> 
                <div className= "mdcol-span3">{activeTab === "appointments" && renderAppointmentsSection()}</div> 
            </div> 
        </div> 
    );
};

export default Dashboard;
