// Dashboard.js
import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { 
  getFirestore, 
  collection, 
  query, 
  where, 
  getDocs, 
  updateDoc, 
  addDoc, 
  doc 
} from 'firebase/firestore';
import { 
  getAuth, 
  signInWithEmailAndPassword, 
  createUserWithEmailAndPassword,
  onAuthStateChanged
} from 'firebase/auth';
import { db, auth } from './firebase'; // Se importa de manera correcta

function Dashboard() {
  // Estados de autenticación
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);

  // Estados de la aplicación
  const [specialties, setSpecialties] = useState([]);
  const [doctors, setDoctors] = useState([]);
  const [availableSlots, setAvailableSlots] = useState([]);
  const [userAppointments, setUserAppointments] = useState([]);

  // Estados de selección
  const [selectedSpecialty, setSelectedSpecialty] = useState('');
  const [selectedDoctor, setSelectedDoctor] = useState('');

  // Estados de administrador
  const [isAdmin, setIsAdmin] = useState(false);
  const [adminView, setAdminView] = useState('specialties');

  // Efecto para manejar la autenticación
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      if (currentUser) {
        setUser(currentUser);
        setIsAdmin(currentUser.email === 'admin@clinica.com');
        loadSpecialties();
        loadUserAppointments();
      }
    });

    return () => unsubscribe();
  }, []);

  // Funciones de autenticación
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await signInWithEmailAndPassword(auth, email, password);
      setError(null);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      setError(null);
    } catch (err) {
      setError(err.message);
    }
  };

  // Funciones de carga de datos
  const loadSpecialties = async () => {
    try {
      const specialtiesRef = collection(db, 'specialties');
      const specialtiesSnapshot = await getDocs(specialtiesRef);
      const specialtiesList = specialtiesSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setSpecialties(specialtiesList);
    } catch (err) {
      setError(err.message);
    }
  };

  const loadDoctors = async (specialty) => {
    try {
      const doctorsRef = collection(db, 'doctors');
      const q = query(doctorsRef, where('specialty', '==', specialty));
      const doctorsSnapshot = await getDocs(q);
      const doctorsList = doctorsSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setDoctors(doctorsList);
    } catch (err) {
      setError(err.message);
    }
  };

  const loadAvailableSlots = async (doctorId) => {
    try {
      const slotsRef = collection(db, 'appointments');
      const q = query(
        slotsRef, 
        where('doctorId', '==', doctorId),
        where('available', '==', true)
      );
      const slotsSnapshot = await getDocs(q);
      const slotsList = slotsSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setAvailableSlots(slotsList);
    } catch (err) {
      setError(err.message);
    }
  };

  const loadUserAppointments = async () => {
    if (!user) return;
    try {
      const appointmentsRef = collection(db, 'appointments');
      const q = query(
        appointmentsRef, 
        where('userId', '==', user.uid),
        where('available', '==', false)
      );
      const appointmentsSnapshot = await getDocs(q);
      const appointmentsList = appointmentsSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      setUserAppointments(appointmentsList);
    } catch (err) {
      setError(err.message);
    }
  };

  // Funciones de reserva y cancelación
  const bookAppointment = async (slotId) => {
    try {
      const slotRef = doc(db, 'appointments', slotId);
      await updateDoc(slotRef, {
        available: false,
        userId: user.uid
      });
      await loadUserAppointments();
      await loadAvailableSlots(selectedDoctor);
    } catch (err) {
      setError(err.message);
    }
  };

  const cancelAppointment = async (slotId) => {
    try {
      const slotRef = doc(db, 'appointments', slotId);
      await updateDoc(slotRef, {
        available: true,
        userId: null
      });
      await loadUserAppointments();
      await loadAvailableSlots(selectedDoctor);
    } catch (err) {
      setError(err.message);
    }
  };

  // Renderizado de vistas
  const renderLoginView = () => (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header">
              <h2 className="text-center">Iniciar Sesión</h2>
            </div>
            <div className="card-body">
              <form onSubmit={handleLogin}>
                <div className="mb-3">
                  <label htmlFor="email" className="form-label">Correo Electrónico</label>
                  <input 
                    type="email" 
                    className="form-control" 
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required 
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="password" className="form-label">Contraseña</label>
                  <input 
                    type="password" 
                    className="form-control" 
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required 
                  />
                </div>
                {error && (
                  <div className="alert alert-danger">{error}</div>
                )}
                <div className="d-grid gap-2">
                  <button type="submit" className="btn btn-primary">Iniciar Sesión</button>
                  <button 
                    type="button" 
                    className="btn btn-secondary"
                    onClick={handleRegister}
                  >
                    Registrarse
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const renderMainView = () => (
    <div className="container mt-4">
      <div className="row">
        <div className="col-12 d-flex justify-content-between align-items-center mb-4">
          <h1>Reserva de Citas Médicas</h1>
          <div>
            {isAdmin && (
              <button 
                className="btn btn-outline-secondary me-2"
                onClick={() => setAdminView(adminView === 'specialties' ? 'doctors' : 'specialties')}
              >
                Panel Admin
              </button>
            )}
            <button 
              className="btn btn-danger"
              onClick={() => auth.signOut()}
            >
              Cerrar Sesión
            </button>
          </div>
        </div>

        {/* Sección de especialidades y doctores */}
        {adminView === 'specialties' ? (
          <div className="col-12">
            <h3>Especialidades</h3>
            <ul>
              {specialties.map(specialty => (
                <li key={specialty.id} onClick={() => {
                  setSelectedSpecialty(specialty.name);
                  loadDoctors(specialty.name);
                }}>
                  {specialty.name}
                </li>
              ))}
            </ul>
          </div>
        ) : (
          <div className="col-12">
            <h3>Doctores en {selectedSpecialty}</h3>
            <ul>
              {doctors.map(doctor => (
                <li key={doctor.id} onClick={() => {
                  setSelectedDoctor(doctor.id);
                  loadAvailableSlots(doctor.id);
                }}>
                  {doctor.name}
                </li>
              ))}
            </ul>
          </div>
        )}

        {/* Sección de citas disponibles */}
        {availableSlots.length > 0 && (
          <div className="col-12 mt-4">
            <h3>Horarios Disponibles</h3>
            <ul>
              {availableSlots.map(slot => (
                <li key={slot.id}>
                  {slot.time} 
                  <button 
                    className="btn btn-success ms-2"
                    onClick={() => bookAppointment(slot.id)}
                  >
                    Reservar
                  </button>
                </li>
              ))}
            </ul>
          </div>
        )}

        {/* Sección de citas del usuario */}
        {userAppointments.length > 0 && (
          <div className="col-12 mt-4">
            <h3>Mis Citas</h3>
            <ul>
              {userAppointments.map(appointment => (
                <li key={appointment.id}>
                  {appointment.time} - {appointment.doctorName}
                  <button 
                    className="btn btn-danger ms-2"
                    onClick={() => cancelAppointment(appointment.id)}
                  >
                    Cancelar
                  </button>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );

  return user ? renderMainView() : renderLoginView();
}

export default Dashboard;
