import React, { useState } from "react";
import { Stethoscope, User, Calendar, LogOut, Search } from "lucide-react";

const Dashboard = () => {
  const [activeTab, setActiveTab] = useState("appointments");
  const [isModalOpen, setIsModalOpen] = useState(false);

  const renderAppointmentsSection = () => (
    <div className="card shadow p-4 mb-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="text-primary">Mis Citas Médicas</h2>
        <button 
          className="btn btn-success d-flex align-items-center"
          onClick={() => setIsModalOpen(true)}
        >
          <Calendar className="me-2" size={20} />
          Nueva Cita
        </button>
      </div>

      <div className="row g-4">
        {[
          {
            specialty: "Cardiología",
            doctor: "Dr. Carlos Mendoza",
            date: "15 Dic 2024",
            time: "10:30 AM",
            status: "Confirmada",
          },
          {
            specialty: "Dermatología",
            doctor: "Dra. Ana Ruiz",
            date: "22 Dic 2024",
            time: "14:00 PM",
            status: "Pendiente",
          },
        ].map((appointment, index) => (
          <div key={index} className="col-md-6 col-lg-4">
            <div className="card p-3 border shadow-sm">
              <div className="d-flex justify-content-between mb-2">
                <span className="fw-bold text-primary">{appointment.specialty}</span>
                <span
                  className={`badge ${
                    appointment.status === "Confirmada"
                      ? "bg-success text-light"
                      : "bg-warning text-dark"
                  }`}
                >
                  {appointment.status}
                </span>
              </div>
              <p className="mb-1">Dr(a). {appointment.doctor}</p>
              <div className="d-flex justify-content-between text-muted">
                <span>{appointment.date}</span>
                <span>{appointment.time}</span>
              </div>
              <div className="d-flex gap-2 mt-3">
                <button className="btn btn-primary w-100">Detalles</button>
                <button className="btn btn-danger w-100">Cancelar</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  return (
    <div className="container-fluid min-vh-100 bg-light py-4">
      <div className="row g-4">
        {/* Sidebar */}
        <div className="col-12 col-md-3 col-lg-2">
          <div className="card shadow p-3">
            <div className="d-flex align-items-center mb-4">
              <Stethoscope className="me-2 text-primary" size={24} />
              <h1 className="fs-4">MediCenter</h1>
            </div>
            <nav className="nav flex-column gap-2">
              {[
                { icon: Calendar, label: "Mis Citas", value: "appointments" },
                { icon: Search, label: "Buscar Citas", value: "search" },
                { icon: User, label: "Perfil", value: "profile" },
              ].map((item) => (
                <button
                  key={item.value}
                  onClick={() => setActiveTab(item.value)}
                  className={`btn btn-link text-start text-decoration-none w-100 ${
                    activeTab === item.value ? "fw-bold text-primary" : ""
                  }`}
                >
                  <item.icon className="me-2" size={20} />
                  {item.label}
                </button>
              ))}
              <button className="btn btn-link text-start text-danger text-decoration-none">
                <LogOut className="me-2" size={20} />
                Cerrar Sesión
              </button>
            </nav>
          </div>
        </div>

        {/* Main Content */}
        <div className="col-12 col-md-9 col-lg-10">
          {activeTab === "appointments" && renderAppointmentsSection()}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
